mapper.select:
SELECT f.free_id AS freeId, f.title AS title, f.member_id AS memberId, f.created_at AS createdAt, f.item_condition AS itemCondition, f.region_gu AS regionGu, f.region_dong AS regionDong, f.content AS content, f.deal_status AS dealStatus, f.view_count AS viewCount, f.category AS category, m.nickname AS nickname, i.img_url AS imgUrl FROM free f JOIN member m ON f.member_id = m.member_id LEFT JOIN free_img i ON f.free_id = i.free_id AND i.repimg_yn = 'Y'
f.free_id > 0
ORDER BY f.free_id DESC LIMIT #{amount} OFFSET #{offset}

mapper.insert:
INSERT INTO free(
    title, member_id, category, region_gu, created_at,
    region_dong, content, item_condition, deal_status
)
VALUES (
           #{title}, #{memberId}, #{category}, #{regionGu}, now(),
           #{regionDong}, #{content}, #{itemCondition}, #{dealStatus}
       )

mapper.update:
UPDATE free
SET title = #{title}, category = #{category}, region_gu = #{regionGu}, region_dong = #{regionDong}, created_at = now(),
    content = #{content}, item_condition = #{itemCondition}, deal_status = #{dealStatus}
WHERE free_id = #{freeId}

mapper.delete:
DELETE FROM free
WHERE free_id = #{freeId}

    AND member_id = #{memberId}

mapper.update:
UPDATE free
SET view_count = view_count + 1
WHERE free_id = #{freeId}
